  stockActual=<span>{`${"Agotado"}`}</span>
  }else{
  stockActual=cuenta.toString();
  }


function compra1(){
let comprar=""
  if (!cuenta>0){
    comprar="Sin Stock"
    }else{
    comprar="Comprar";
    };
  return comprar}

   
  

   const restar = () => {
    if (cuenta>0){
    setcuenta(cuenta - 1)
    props.sumar()
    }
  }

  return (
    <div className='producto'>
      <img src={props.imagen} alt={props.nombre}/>
     <h3>{props.nombre}</h3>
             <p>{props.descripcion}</p>
             <h5>En Stock:{stock()}</h5>
             <button onClick={restar}>{compra1()}</button>
    </div>
  )
}

    

export default Item;
